INSERT INTO `additives` (`id`, `name`, `code`, `category`, `safety_level`, `description`, `health_risks`, `created_at`) VALUES
(2, 'Monosodium Glutamat', 'E621', 'perisa', 'moderate', 'Penguat rasa gurih (umami).', 'Dapat memicu sakit kepala atau mual pada orang sensitif (Chinese Restaurant Syndrome).', '2025-11-20 23:57:45'),
(4, 'Aspartam', 'E951', 'pemanis', 'moderate', 'Pemanis buatan rendah kalori, 200x lebih manis dari gula.', 'Berbahaya bagi penderita fenilketonuria (PKU). Kontroversial terkait efek saraf.', '2025-11-20 23:57:45'),
(6, 'Natrium Benzoat', 'E211', 'pengawet', 'safe', 'Mencegah jamur pada makanan asam (minuman soda, saus).', 'Bisa membentuk benzena (karsinogen) jika bereaksi dengan Vitamin C.', '2025-11-20 23:57:45'),
(8, 'Tartrazin', 'E102', 'pewarna', 'avoid', 'Pewarna kuning sintetis (Lemon Yellow).', 'Dapat memicu hiperaktivitas pada anak (ADHD) dan reaksi alergi.', '2025-11-20 23:57:45'),
(10, 'Sukralosa', 'E955', 'pemanis', 'safe', 'Pemanis buatan dari gula yang diklorinasi.', 'Umumnya aman, tidak menaikkan gula darah, stabil pada suhu panas.', '2025-11-20 23:57:45'),
(12, 'BHA (Butylated Hydroxyanisole)', 'E320', 'pengawet', 'avoid', 'Antioksidan sintetis untuk mencegah lemak tengik.', 'Diklasifikasikan sebagai kemungkinan karsinogen (penyebab kanker) pada manusia.', '2025-11-20 23:57:45'),
(14, 'BHT (Butylated Hydroxytoluene)', 'E321', 'pengawet', 'avoid', 'Sering dipakai bersama BHA.', 'Terkait dengan gangguan hormon dan potensi karsinogenik.', '2025-11-20 23:57:45'),
(16, 'Karagenan', 'E407', 'lainnya', 'moderate', 'Pengental dari rumput laut.', 'Dapat memicu peradangan usus atau gangguan pencernaan pada sebagian orang.', '2025-11-20 23:57:45'),
(18, 'Sirup Jagung Tinggi Fruktosa (HFCS)', '-', 'pemanis', 'avoid', 'Pemanis cair dari pati jagung.', 'Kontributor utama obesitas, diabetes, dan perlemakan hati.', '2025-11-20 23:57:45'),
(20, 'Nitrit & Nitrat', 'E249-252', 'pengawet', 'avoid', 'Pengawet daging olahan (sosis, kornet) agar tetap merah.', 'Dapat berubah menjadi nitrosamin yang bersifat karsinogenik.', '2025-11-20 23:57:45'),
(22, 'Merah Allura', 'E129', 'pewarna', 'moderate', 'Pewarna merah sintetis.', 'Dilarang di beberapa negara Eropa, terkait dengan hiperaktivitas.', '2025-11-20 23:57:45'),
(24, 'Asam Askorbat', 'E300', 'pengawet', 'safe', 'Vitamin C sintetis.', 'Sangat aman, berfungsi sebagai antioksidan.', '2025-11-20 23:57:45');
