INSERT INTO `diseases` (`id`, `name`, `description`, `dietary_recommendations`, `foods_to_avoid`, `created_at`) VALUES
(2, 'Diabetes Melitus Tipe 2', 'Kadar gula darah tinggi akibat resistensi insulin.', 'Fokus pada Karbohidrat Kompleks, Serat Tinggi, Indeks Glikemik Rendah.', 'Gula pasir, minuman manis, roti putih, nasi putih berlebih, sirup.', '2025-11-20 23:57:45'),
(4, 'Hipertensi (Darah Tinggi)', 'Tekanan darah arteri yang persisten tinggi.', 'Diet DASH: Tinggi Kalium, Kalsium, Magnesium. Perbanyak sayur & buah.', 'Garam (Natrium), makanan kaleng, ikan asin, keripik asin, MSG berlebih.', '2025-11-20 23:57:45'),
(6, 'Dislipidemia (Kolesterol)', 'Kadar lemak darah (LDL/Trigliserida) tidak normal.', 'Tingkatkan lemak tak jenuh (Omega-3), serat larut (Oat).', 'Lemak trans (margarin, gorengan), lemak jenuh (kulit ayam, santan kental), jeroan.', '2025-11-20 23:57:45'),
(8, 'Gagal Ginjal Kronis', 'Penurunan fungsi ginjal bertahap.', 'Batasi Protein, Natrium, Kalium, dan Fosfor sesuai stadium.', 'Buah tinggi kalium (pisang, alpukat), produk susu, makanan olahan.', '2025-11-20 23:57:45'),
(10, 'Asam Urat (Gout)', 'Penumpukan kristal asam urat di sendi.', 'Diet rendah purin, banyak minum air putih, konsumsi Vitamin C.', 'Jeroan (hati, usus), daging merah, seafood (udang, kerang), emping, alkohol.', '2025-11-20 23:57:45'),
(12, 'GERD (Asam Lambung)', 'Naiknya asam lambung ke kerongkongan.', 'Makan porsi kecil tapi sering, hindari pemicu iritasi.', 'Makanan pedas, asam, berlemak tinggi, kafein, cokelat, mint.', '2025-11-20 23:57:45'),
(14, 'Celiac Disease', 'Reaksi imun terhadap gluten.', 'Wajib Diet Bebas Gluten (Gluten-Free) seumur hidup.', 'Gandum, barley, rye, pasta biasa, roti biasa, kecap (yang mengandung gandum).', '2025-11-20 23:57:45'),
(16, 'Intoleransi Laktosa', 'Ketidakmampuan mencerna gula susu.', 'Gunakan produk bebas laktosa atau susu nabati (Almond, Soy).', 'Susu sapi murni, krim, es krim, keju lunak.', '2025-11-20 23:57:45'),
(18, 'Anemia Defisiensi Besi', 'Kekurangan sel darah merah akibat kurang zat besi.', 'Tingkatkan asupan Zat Besi Heme dan Vitamin C (untuk penyerapan).', 'Teh/Kopi saat makan (menghambat penyerapan), kalsium berlebih saat makan.', '2025-11-20 23:57:45'),
(20, 'Obesitas', 'Akumulasi lemak tubuh berlebih.', 'Defisit Kalori, tingkatkan protein dan serat, hindari gula cair.', 'Makanan cepat saji, minuman bergula, camilan tinggi kalori, gorengan.', '2025-11-20 23:57:45');
INSERT INTO `education_articles` (`id`, `title`, `slug`, `category`, `content`, `author`, `thumbnail_url`, `read_time`, `view_count`, `is_published`, `created_at`, `updated_at`) VALUES
(2, 'Makronutrien: Kunci Metabolisme Sehat', 'makronutrien-kunci-metabolisme', 'gizi', '<p>Makronutrien (Karbohidrat, Protein, Lemak) adalah sumber energi utama. Keseimbangan ketiganya sangat penting untuk fungsi tubuh dan metabolisme yang optimal.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Makronutrien', '5 min', 2, 1, '2025-11-23 20:30:34', '2025-11-23 20:31:07'),
(4, 'Vitamin Larut Air vs Larut Lemak', 'vitamin-larut-air-lemak', 'gizi', '<p>Vitamin larut air (B dan C) harus dikonsumsi harian karena tidak disimpan tubuh. Vitamin larut lemak (A, D, E, K) dapat disimpan di hati dan jaringan lemak.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Vitamin', '4 min', 2, 1, '2025-11-23 20:30:34', '2025-11-23 22:13:31'),
(6, 'Zat Besi: Cegah Anemia pada Remaja', 'zat-besi-cegah-anemia', 'gizi', '<p>Konsumsi hati ayam, daging merah, atau bayam. Minum jus jeruk saat makan daging untuk meningkatkan penyerapan zat besi hingga 3x lipat.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Zat+Besi', '5 min', 2, 1, '2025-11-23 20:30:34', '2025-11-23 20:31:09'),
(8, 'Manfaat Omega-3 untuk Otak Anak', 'manfaat-omega-3-otak', 'gizi', '<p>Asam lemak Omega-3 (EPA & DHA) krusial untuk perkembangan kognitif. Sumber terbaik: Ikan Salmon, Sarden, Telur Omega, dan Kenari.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Omega+3', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(10, 'Kalsium Bukan Hanya Susu', 'kalsium-sumber-nabati', 'gizi', '<p>Bagi yang intoleransi laktosa, kalsium bisa didapat dari: Brokoli, Tahu, Tempe, Kacang Almond, dan Ikan Teri (dimakan dengan tulang).</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Kalsium', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(12, 'Bahaya Diet Ekstrem Tanpa Karbohidrat', 'bahaya-diet-ekstrem', 'gizi', '<p>Menghilangkan karbohidrat total bisa menyebabkan ketoasidosis, bau mulut, dan kelelahan kronis. Tubuh membutuhkan karbohidrat sebagai energi utama.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Diet+Sehat', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(14, 'Mengenal Lemak Trans: Musuh Jantung', 'mengenal-lemak-trans', 'gizi', '<p>Lemak trans meningkatkan kolesterol jahat (LDL) dan menurunkan kolesterol baik (HDL). Hindari produk dengan komposisi \"Minyak Terhidrogenasi Parsial\".</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Lemak+Trans', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(16, 'Protein Hewani vs Nabati: Mana Lebih Baik?', 'protein-hewani-vs-nabati', 'gizi', '<p>Protein hewani lengkap, nabati kaya serat. Kombinasi keduanya adalah yang terbaik untuk kesehatan jangka panjang.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Protein', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(18, 'Pentingnya Vitamin D: Vitamin Matahari', 'pentingnya-vitamin-d', 'gizi', '<p>Vitamin D membantu penyerapan kalsium. Defisiensi menyebabkan tulang lunak. Berjemurlah 10-15 menit sebelum jam 10 pagi.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Vitamin+D', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(20, 'Serat Pangan: Sahabat Pencernaan', 'serat-pangan-pencernaan', 'gizi', '<p>Serat melancarkan pencernaan dan mengontrol gula darah. Serat larut (oat) baik untuk kolesterol, serat tak larut (sayur) baik untuk BAB.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Serat+Pangan', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(22, 'Peran Utama Air dalam Gizi Seimbang', 'peran-air-gizi', 'gizi', '<p>Air adalah makronutrien yang sering dilupakan. Air membantu transportasi nutrisi, mengatur suhu tubuh, dan melumasi sendi. Dehidrasi memperlambat metabolisme.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Air+Hidrasi', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(24, 'Mengenal Mikronutrien Penting', 'mengenal-mikronutrien', 'gizi', '<p>Mikronutrien (vitamin dan mineral) diperlukan dalam jumlah kecil tetapi sangat vital untuk fungsi enzim, sistem imun, dan kesehatan sel. Sumber: buah, sayur, kacang-kacangan.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Mikronutrien', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(26, 'Karbohidrat Kompleks vs Sederhana', 'karbohidrat-kompleks-sederhana', 'gizi', '<p>Karbohidrat kompleks (gandum utuh, beras merah) dicerna lebih lambat, memberikan energi stabil. Sederhana (gula, tepung putih) menyebabkan lonjakan gula darah.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Karbohidrat', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(28, 'Menghitung Kalori Tersembunyi', 'menghitung-kalori-tersembunyi', 'gizi', '<p>Kalori tersembunyi sering berasal dari minuman manis, saus salad, dan makanan *deep-fry*. Selalu cek label dan hindari makanan cair berkalori tinggi.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Kalori', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(30, 'Pentingnya Antioksidan Alami', 'pentingnya-antioksidan', 'gizi', '<p>Antioksidan (Vitamin C, E, Beta-Karoten) melawan radikal bebas dalam tubuh, mengurangi stres oksidatif, dan mencegah penuaan dini serta penyakit kronis.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Antioksidan', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(32, 'Diet Seimbang untuk Vegetarian', 'diet-seimbang-vegetarian', 'gizi', '<p>Diet vegetarian harus fokus pada sumber Protein non-hewani (tahu, tempe, kacang), Zat Besi, Kalsium, dan Vitamin B12 (lewat fortifikasi/suplemen).</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Vegetarian', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(34, 'Perbedaan Utama Kolesterol HDL dan LDL', 'hdl-vs-ldl', 'gizi', '<p>HDL (baik) membersihkan kolesterol dari arteri. LDL (jahat) menyumbat arteri. Tingkatkan HDL dengan lemak tak jenuh dan olahraga, kurangi LDL dengan menghindari lemak trans/jenuh.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Kolesterol+HDL', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(36, 'Pola Makan Sehat Anak Usia Sekolah', 'pola-makan-anak', 'gizi', '<p>Anak memerlukan Protein dan Kalsium yang cukup untuk pertumbuhan. Batasi gula tambahan (permen, soda) dan biasakan sarapan sehat.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Gizi+Anak', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(38, 'Bahaya Kurangnya Garam dalam Tubuh', 'bahaya-kurang-garam', 'gizi', '<p>Meskipun Natrium harus dibatasi, kekurangan Natrium (Hyponatremia) juga berbahaya, terutama saat olahraga ekstrem. Natrium penting untuk keseimbangan cairan dan fungsi saraf.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Garam', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(40, 'Mitos Populer Tentang Kalori', 'mitos-kalori', 'gizi', '<p>Mitos: Semua kalori sama. Fakta: Kalori dari protein memberikan rasa kenyang lebih lama daripada kalori dari gula. Sumber kalori sangat menentukan kualitas diet.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/A1D2D5/333?text=Mitos+Kalori', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(42, 'Bahaya Gula Tersembunyi: HFCS', 'bahaya-gula-tersembunyi-aditif', 'aditif', '<p>Sirup Jagung Tinggi Fruktosa (HFCS) adalah pemanis murah yang sering dipakai di industri. Konsumsi berlebih memicu perlemakan hati (Fatty Liver), resistensi insulin, dan obesitas.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=HFCS', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(44, 'MSG: Kawan atau Lawan?', 'msg-kawan-atau-lawan', 'aditif', '<p>Monosodium Glutamat (MSG) mengandung natrium dan glutamat. FDA dan BPOM menggolongkannya aman (GRAS). Masalah utamanya seringkali pada makanan yang rendah nutrisi.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=MSG', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(46, 'Pewarna Makanan: Alami vs Sintetis', 'pewarna-alami-sintetis', 'aditif', '<p>Pewarna sintetis (Tartrazin, Karmoisin) harus dibatasi. Studi menunjukkan kaitan pewarna sintetis tertentu dengan hiperaktivitas anak. Pewarna alami (kunyit, pandan) lebih aman.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Pewarna+Sintetis', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(48, 'Pemanis Buatan: Aspartam vs Stevia', 'aspartam-vs-stevia', 'aditif', '<p>Aspartam adalah sintetis dan tidak tahan panas. Stevia adalah alami dan aman. Untuk penderita PKU, wajib menghindari Aspartam.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Pemanis+Buatan', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(50, 'Bahaya Nitrit pada Daging Olahan', 'bahaya-nitrit-daging-olahan', 'aditif', '<p>Natrium Nitrit membuat daging tetap merah. Jika dipanaskan suhu tinggi, bisa membentuk nitrosamin karsinogenik. Batasi konsumsi sosis bakar atau daging olahan.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Nitrit', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(52, 'Apa itu Pengemulsi Lesitin Kedelai?', 'pengemulsi-lesitin-kedelai', 'aditif', '<p>Lesitin kedelai (E322) sering ditemukan di cokelat. Fungsinya menyatukan air dan lemak. Ini aman, tetapi perlu diwaspadai bagi yang alergi kedelai.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Lesitin', '2 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(54, 'Pengawet Berbahaya: BHA dan BHT', 'pengawet-bha-bht', 'aditif', '<p>BHA (E320) dan BHT (E321) adalah antioksidan sintetis. Keduanya sebaiknya dihindari karena terkait dengan risiko kesehatan dan gangguan hormon.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=BHA+BHT', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(56, 'Pentingnya Asam Sitrat (E330)', 'asam-sitrat-e330', 'aditif', '<p>Asam Sitrat (E330) adalah pengatur keasaman yang umum dan aman. Konsumsi berlebihan saja yang dapat mengikis email gigi.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Asam+Sitrat', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(58, 'Aditif Peningkat Volume: Karagenan', 'karagenan-e407', 'aditif', '<p>Karagenan (E407) adalah pengental dari rumput laut. Konsumsi dalam jumlah besar dapat memicu peradangan atau gangguan pencernaan pada orang sensitif.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Karagenan', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(60, 'Aditif Anti-Kempal: Silikon Dioksida', 'anti-kempal-silikon', 'aditif', '<p>Silikon Dioksida (E551) adalah anti-kempal pada bumbu bubuk. Umumnya dianggap aman, berfungsi mencegah bubuk menggumpal karena kelembaban.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Anti+Kempal', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(62, 'Bahaya Natrium Benzoat dan Vitamin C', 'natrium-benzoat-vc', 'aditif', '<p>Natrium Benzoat (E211) adalah pengawet. Jika bereaksi dengan Vitamin C, dapat membentuk Benzena yang bersifat karsinogen. Waspadai kombinasi ini pada minuman soda.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Natrium+Benzoat', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(64, 'Perisa Buatan: Apakah Aman Dikonsumsi?', 'perisa-buatan-keamanan', 'aditif', '<p>Perisa buatan (artificial flavor) umumnya aman, tetapi tidak memberikan nilai nutrisi. Beberapa orang mungkin mengalami reaksi sensitif, seperti migrain.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Perisa+Buatan', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(66, 'Pengemulsi Mono dan Digliserida', 'pengemulsi-mono-digliserida', 'aditif', '<p>Mono- dan Digliserida (E471) adalah pengemulsi yang umum di produk *bakery*. Bahan ini sering berasal dari sumber nabati atau hewani (lemak babi) yang perlu diwaspadai.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=E471', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(68, 'Keamanan Pewarna Merah Allura (E129)', 'pewarna-merah-allura', 'aditif', '<p>Pewarna Merah Allura (E129) adalah pewarna sintetis yang dilarang di beberapa negara. Terkait dengan potensi hiperaktivitas pada anak, sebaiknya dihindari.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Merah+E129', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(70, 'Aspartam dan Risiko Fenilketonuria (PKU)', 'aspartam-risiko-pku', 'aditif', '<p>Aspartam mengandung fenilalanin, zat yang berbahaya bagi penderita Fenilketonuria (PKU). Wajib ada peringatan di label untuk produk mengandung Aspartam.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=PKU', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(72, 'Kaitan Pewarna Biru (E133) dan Alergi', 'pewarna-biru-e133', 'aditif', '<p>Pewarna Biru Cemerlang (E133) adalah pewarna sintetis. Meskipun aman bagi sebagian besar, ia dapat memicu reaksi alergi pada penderita asma yang sensitif terhadap aspirin.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Pewarna+Biru', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(74, 'Peran Pengembang Adonan (Ragi dan Baking Powder)', 'pengembang-adonan', 'aditif', '<p>Pengembang adonan (seperti ragi, *baking powder*, *baking soda*) adalah aditif yang membantu adonan mengembang. Umumnya aman, tetapi mengandung natrium.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Pengembang', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(76, 'Aditif Pada Produk Kedelai (Tahu/Tempe)', 'aditif-produk-kedelai', 'aditif', '<p>Produk kedelai seperti tahu dan tempe menggunakan pengasam (koagulan) untuk pembentukan. Pengemulsi lesitin juga umum ada. Cek sumber *koagulan* jika Anda sensitif.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Kedelai+Aditif', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(78, 'Transglutaminase: Perekat Daging', 'transglutaminase-daging', 'aditif', '<p>Transglutaminase, sering disebut *meat glue*, adalah enzim yang digunakan untuk merekatkan potongan daging. Umumnya aman, namun meningkatkan risiko terpapar bakteri jika tidak dimasak sempurna.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=Meat+Glue', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(80, 'Mengenal Kode E Pada Pemanis Alami', 'kode-e-pemanis-alami', 'aditif', '<p>Beberapa pemanis alami juga memiliki kode E, seperti Stevia (E960) atau Erythritol (E968). Kode ini hanya menandakan statusnya sebagai aditif yang disetujui, bukan berarti sintetis.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FFC107/333?text=E960', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(82, '5 Tips Diet Hipertensi (DASH Diet)', 'tips-diet-hipertensi', 'penyakit', '<p>Diet DASH terbukti menurunkan tekanan darah. Prinsipnya: 1) Batasi garam < 1 sdt/hari. 2) Perbanyak Kalium. 3) Hindari makanan kaleng dan daging olahan.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Hipertensi', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(84, 'Pola Makan untuk Asam Urat', 'diet-rendah-purin', 'penyakit', '<p>Hindari jeroan, melinjo, dan seafood berlebih. Perbanyak minum air putih untuk membuang kristal asam urat melalui urin.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Asam+Urat', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(86, 'Kolesterol Tinggi pada Usia Muda', 'kolesterol-usia-muda', 'penyakit', '<p>Gaya hidup sedenter dan *fast food* memicu kolesterol muda. Kurangi gorengan, perbanyak oat dan olahraga teratur.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Kolesterol', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(88, 'GERD: Makanan Pemicu dan Pencegahan', 'makanan-pemicu-gerd', 'penyakit', '<p>Penderita GERD harus menghindari: Kopi, Cokelat, Makanan Pedas, dan Buah Asam. Jangan langsung tidur setelah makan.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=GERD', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(90, 'Cara Memilih Camilan untuk Diabetes', 'camilan-diabetes-penyakit', 'penyakit', '<p>Penderita diabetes tetap boleh ngemil. Kuncinya: Serat & Protein. Pilihan baik: Kacang almond, Yoghurt plain, Apel dengan selai kacang.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Diabetes+Snack', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(92, 'Apa Itu Celiac Disease dan Gluten Free', 'apa-itu-celiac-diet', 'penyakit', '<p>Celiac bukan alergi, tapi penyakit autoimun. Solusi satu-satunya adalah menghindari gandum, rye, dan barley seumur hidup.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Celiac+Diet', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(94, 'Diet Rendah Kalium untuk Gagal Ginjal', 'diet-gagal-ginjal', 'penyakit', '<p>Pada gagal ginjal, penting untuk membatasi Protein, Natrium, Kalium, dan Fosfor. Hindari buah tinggi kalium seperti pisang dan alpukat.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Gagal+Ginjal', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(96, 'Intoleransi Laktosa: Mengenal Alternatif Susu', 'intoleransi-laktosa-diet', 'penyakit', '<p>Ketidakmampuan mencerna gula susu. Gunakan produk bebas laktosa atau susu nabati (Almond, Soy) sebagai pengganti.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Laktosa+Diet', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(98, 'Obesitas: Prinsip Utama Defisit Kalori', 'prinsip-defisit-kalori', 'penyakit', '<p>Penurunan berat badan dimulai dari defisit kalori. Tingkatkan asupan protein dan serat, hindari minuman bergula dan *fast food*.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Obesitas+Diet', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(100, 'Penyakit Autoimun: Peran Diet Anti-Inflamasi', 'diet-anti-inflamasi', 'penyakit', '<p>Diet anti-inflamasi (tinggi Omega-3, sayur, dan buah) dapat membantu meredakan gejala penyakit autoimun seperti Lupus atau Rheumatoid Arthritis.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Autoimun+Diet', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(102, 'Anemia Defisiensi Besi: Diet Kaya Fe', 'anemia-defisiensi-besi', 'penyakit', '<p>Fokus pada makanan kaya Zat Besi Heme (daging merah, hati) dan non-Heme (bayam, kacang-kacangan) yang dikombinasikan dengan Vitamin C.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Anemia+Diet', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(104, 'Sindrom Metabolik: Mengatur Tiga Faktor Risiko', 'sindrom-metabolik-diet', 'penyakit', '<p>Sindrom metabolik melibatkan kolesterol, tekanan darah, dan gula darah. Diet harus fokus pada biji-bijian utuh, protein tanpa lemak, dan membatasi gula.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Metabolik', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(106, 'Batu Ginjal: Pentingnya Batasi Oksalat dan Garam', 'batu-ginjal-diet', 'penyakit', '<p>Untuk batu ginjal jenis kalsium oksalat, batasi makanan tinggi oksalat (bayam, almond, cokelat). Yang terpenting: tingkatkan asupan cairan dan kurangi natrium.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Batu+Ginjal', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(108, 'Diet Rendah FODMAP untuk IBS', 'diet-rendah-fodmap', 'penyakit', '<p>Diet FODMAP (Fermentable Oligosaccharides, Disaccharides, Monosaccharides, and Polyols) digunakan untuk meredakan gejala *Irritable Bowel Syndrome* (IBS) dengan membatasi karbohidrat rantai pendek.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=FODMAP', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(110, 'Osteoporosis: Diet Kaya Kalsium dan D', 'osteoporosis-diet', 'penyakit', '<p>Pencegahan osteoporosis memerlukan asupan Kalsium (susu, sayuran hijau) dan Vitamin D (paparan matahari, ikan) yang memadai sejak usia muda.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Osteoporosis', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(112, 'Peran Diet pada Pencegahan Kanker', 'diet-pencegahan-kanker', 'penyakit', '<p>Diet kaya buah, sayur, dan biji-bijian utuh (antioksidan, serat) dapat menurunkan risiko kanker. Hindari daging olahan dan alkohol berlebihan.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Anti+Kanker', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(114, 'Diet Ketogenik: Panduan Dasar dan Risiko', 'diet-ketogenik-panduan', 'penyakit', '<p>Diet Keto memaksa tubuh membakar lemak (ketosis) dengan membatasi karbohidrat sangat rendah. Harus dilakukan di bawah pengawasan karena risiko defisiensi nutrisi.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Keto', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(116, 'Gout Akut: Manajemen Serangan Asam Urat', 'gout-akut-manajemen', 'penyakit', '<p>Saat serangan Gout, selain obat, fokus pada air putih dan hindari semua pemicu purin tinggi (jeroan, daging merah, *seafood*). Konsumsi cherry dapat membantu.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Gout', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(118, 'Mengenal Alergi Makanan: Gejala dan Penanganan', 'mengenal-alergi-makanan', 'penyakit', '<p>Alergi makanan dipicu oleh respons imun terhadap protein makanan (misal: protein susu). Gejalanya cepat (gatal, bengkak, sesak). Wajib eliminasi pemicunya.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Alergi+Makanan', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(120, 'Kesehatan Mikrobioma Usus dan Diet', 'kesehatan-mikrobioma', 'penyakit', '<p>Mikrobioma usus memengaruhi imun dan mood. Jaga kesehatan usus dengan makanan kaya serat (prebiotik) dan makanan fermentasi (probiotik) seperti yogurt dan tempe.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/EF5350/white?text=Mikrobioma', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(122, 'Panduan Membaca Label Informasi Nilai Gizi', 'panduan-membaca-label', 'label', '<p>Perhatikan <strong>Takaran Saji</strong> terlebih dahulu. Angka kalori adalah per sajian. Cek %AKG: di bawah 5% berarti rendah, di atas 20% berarti tinggi. Pilih %AKG rendah untuk lemak jenuh, gula, dan garam.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Label+Gizi', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(124, 'Cara Cek Kedaluwarsa & Kode Produksi', 'cek-kedaluwarsa-kode', 'label', '<p>Expired Date (Kedaluwarsa): terkait keamanan. Best Before (Baik digunakan sebelum): terkait kualitas rasa. Selalu utamakan Expired Date.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Cek+Exp', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(126, 'Memahami Kode E-Number pada Komposisi', 'memahami-kode-e-number', 'label', '<p>Kode E-Number (E621, E951, dll.) adalah kode standar Eropa untuk aditif makanan. Kami menyediakan kamus aditif untuk mengidentifikasi tingkat keamanannya.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=E+Number', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(128, 'Logo Pilihan Lebih Sehat', 'logo-pilihan-lebih-sehat', 'label', '<p>Cari logo centang hijau \"Pilihan Lebih Sehat\" dari BPOM. Ini menjamin gula, garam, dan lemaknya lebih rendah dari produk sejenis.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Logo+Sehat', '2 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(130, 'Mengurai Daftar Komposisi Terpanjang', 'mengurai-komposisi', 'label', '<p>Bahan yang dicantumkan paling awal adalah yang paling banyak kandungannya. Hindari produk dengan gula, minyak, atau natrium di urutan 3 teratas.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Komposisi', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(132, 'Cara Cek Registrasi BPOM Resmi', 'cek-registrasi-bpom', 'label', '<p>Setiap produk impor atau lokal yang beredar wajib memiliki nomor MD/ML. Gunakan fitur scanner BPOM kami untuk verifikasi keaslian produk.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Cek+BPOM', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(134, 'Mengenali Klaim \"Bebas Gula\"', 'klaim-bebas-gula', 'label', '<p>Klaim \"Bebas Gula\" belum tentu bebas kalori. Seringkali diganti dengan pemanis buatan yang juga perlu diperhatikan dampaknya bagi tubuh.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Bebas+Gula', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(136, 'Alergen Mayor: Waspadai 8 Bahan Utama', 'alergen-mayor-8', 'label', '<p>Ada 8 alergen utama (Big 8): Susu, Telur, Kacang Tanah, Kacang Pohon, Gandum, Kedelai, Ikan, dan Kerang. Wajib dicantumkan di label kemasan.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Alergen+Mayor', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(138, 'Porsi: Kenapa Penting Diperhatikan?', 'pentingnya-porsi', 'label', '<p>Jika label mengatakan 1 sajian = 100 kkal dan kemasan berisi 3 sajian, maka total yang Anda konsumsi adalah 300 kkal. Porsi adalah kunci perhitungan yang akurat.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Porsi+Sajian', '2 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(140, 'Lemak Jenuh vs Lemak Tak Jenuh', 'lemak-jenuh-tak-jenuh', 'label', '<p>Batasi Lemak Jenuh (tinggi kolesterol). Pilih Lemak Tak Jenuh (Minyak zaitun, alpukat) untuk kesehatan jantung. Selalu bandingkan di label gizi.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Jenis+Lemak', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(142, 'Memahami %AKG (Angka Kecukupan Gizi)', 'memahami-persen-akg', 'label', '<p>Persen AKG menunjukkan kontribusi nutrisi dalam satu sajian terhadap kebutuhan harian 2150 kkal. Gunakan sebagai panduan cepat untuk menilai baik/buruknya produk.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Persen+AKG', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(144, 'Perhitungan Kalori per 100g', 'kalori-per-100g', 'label', '<p>Untuk membandingkan dua produk, selalu konversi nilai gizi ke per 100g. Ini memberikan gambaran yang adil tentang kepadatan kalori dan nutrisi produk.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Kalori+100g', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(146, 'Mengenali Klaim \"Organik\"', 'klaim-organik', 'label', '<p>Klaim \"Organik\" menunjukkan bahwa bahan ditanam tanpa pestisida sintetis. Namun, produk organik tetap harus dibatasi jika tinggi gula atau lemak.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Organik', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(148, 'Klaim \"Rendah Lemak\" yang Menyesatkan', 'klaim-rendah-lemak', 'label', '<p>Produk yang diklaim \"Rendah Lemak\" seringkali mengganti lemak dengan gula atau natrium untuk mempertahankan rasa. Selalu periksa kolom Gula dan Natrium.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Rendah+Lemak', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(150, 'Dampak Pengemasan pada Nilai Gizi', 'pengemasan-nilai-gizi', 'label', '<p>Jenis kemasan dapat memengaruhi umur simpan dan kerusakan nutrisi. Kemasan kedap udara dan gelap menjaga vitamin dan antioksidan lebih baik.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Pengemasan', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(152, 'Memahami Simbol Peringatan Alergi', 'simbol-peringatan-alergi', 'label', '<p>Selalu cari tulisan *May contain traces of...* (Dapat mengandung jejak...) jika Anda memiliki alergi parah, karena risiko kontaminasi silang sangat mungkin terjadi.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Simbol+Alergi', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(154, 'Transparansi Bahan Olahan Susu', 'bahan-olahan-susu', 'label', '<p>Intoleransi laktosa atau alergi susu harus mewaspadai *whey*, *kasein*, dan *kaseinat* di daftar komposisi, karena ini adalah turunan protein susu.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Susu+Olahan', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(156, 'Membedakan Pemanis Alami vs Buatan pada Label', 'pemanis-label', 'label', '<p>Pemanis alami (Stevia, Madu, Gula Aren) memiliki nama yang jelas. Pemanis buatan sering berupa Aspartam, Sukralosa, atau Sakarin (ditandai dengan E-Number).</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Pemanis+Label', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(158, 'Regulasi Klaim \"Gluten-Free\"', 'regulasi-gluten-free', 'label', '<p>Produk dengan klaim \"Gluten-Free\" harus mengandung kurang dari 20 ppm (parts per million) gluten. Ini aman bagi penderita Celiac.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=Gluten+Free', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(160, 'Cara Cek Keamanan Makanan Impor', 'keamanan-makanan-impor', 'label', '<p>Makanan impor harus memiliki label ML (Makanan Luar) dari BPOM. Nomor ML menjamin produk tersebut telah melalui pemeriksaan keamanan pangan Indonesia.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/FF9966/white?text=ML+BPOM', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(162, 'Camilan Sehat untuk Penderita Diabetes', 'camilan-diabetes-tips', 'tips', '<p>Pilih camilan kaya serat & protein seperti kacang almond (tanpa garam), yoghurt plain, atau apel dengan selai kacang. Hindari camilan tepung murni.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Snack+Diabetes', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(164, 'Air Putih: Kebutuhan Ideal Harian', 'kebutuhan-air-putih', 'tips', '<p>Minimal 8 gelas (2 liter). Cek warna urin: jika kuning pekat, Anda kurang minum. Tingkatkan asupan saat berolahraga atau di cuaca panas.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Hidrasi', '2 min', 2, 1, '2025-11-23 20:30:34', '2025-11-23 20:39:55'),
(166, 'Cara Mencuci Sayur Bebas Pestisida', 'cuci-sayur-bersih', 'tips', '<p>Cuci di air mengalir minimal 30 detik. Bisa gunakan larutan air garam atau cuka apel (1 bagian cuka: 3 bagian air) untuk mengurangi residu pestisida.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Cuci+Sayur', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(168, 'Meal Prep: Hemat & Sehat', 'tips-meal-prep', 'tips', '<p>Siapkan bahan makanan untuk 3 hari ke depan di akhir pekan. Ini mencegah jajan sembarangan saat lapar mendadak dan membantu mengontrol porsi.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Meal+Prep', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(170, 'Mengatasi Ngidam Makanan Manis', 'atasi-ngidam-manis', 'tips', '<p>Coba alihkan ke buah segar atau *dark chocolate*. Kurangi gula secara bertahap agar tubuh Anda terbiasa dengan rasa yang kurang manis.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Ngidam+Manis', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(172, 'Tidur yang Cukup untuk Kontrol Berat Badan', 'tidur-kontrol-berat', 'tips', '<p>Kurang tidur meningkatkan hormon Ghrelin (lapar). Tidur 7-8 jam per malam sangat penting untuk diet yang sukses dan menstabilkan nafsu makan.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Tidur+Sehat', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(174, 'Pentingnya Sarapan Penuh Protein', 'sarapan-protein', 'tips', '<p>Sarapan dengan protein (telur, yoghurt) membuat Anda kenyang lebih lama, mengurangi ngemil di siang hari, dan menstabilkan gula darah.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Sarapan', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(176, 'Tips Belanja Makanan Sehat di Supermarket', 'tips-belanja-sehat', 'tips', '<p>Selalu belanja di pinggiran (sayur, buah, daging segar) dan hindari lorong tengah (makanan olahan). Selalu cek label nutrisi sebelum membeli.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Belanja+Sehat', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(178, 'Mencari Keseimbangan Karbohidrat', 'jangan-takut-karbo-tips', 'tips', '<p>Pilih karbohidrat kompleks (oat, beras merah) daripada karbohidrat sederhana. Karbohidrat adalah energi utama otak Anda.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Karbohidrat', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(180, 'Diet Saat Puasa: Jaga Hidrasi & Serat', 'diet-saat-puasa', 'tips', '<p>Saat berpuasa, fokuskan asupan cairan dan serat saat sahur dan berbuka. Hindari makanan terlalu manis atau asin saat sahur.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Puasa+Sehat', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(182, 'Tips Mengurangi Garam Saat Memasak', 'tips-kurangi-garam', 'tips', '<p>Gunakan bumbu alami (herbal, rempah-rempah, lemon) sebagai pengganti garam. Kurangi penggunaan saus kemasan karena tinggi natrium.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Kurangi+Garam', '3 min', 2, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:57'),
(184, 'Cara Memasak Sehat: Rebus, Kukus, Panggang', 'cara-memasak-sehat', 'tips', '<p>Hindari metode *deep-fry* (goreng rendam). Utamakan merebus, mengukus, atau memanggang untuk mengurangi asupan lemak berlebih.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Memasak+Sehat', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(186, 'Makan di Luar: Pilih Menu Paling Sehat', 'makan-di-luar-sehat', 'tips', '<p>Saat makan di restoran, pesan saus terpisah, pilih menu yang dipanggang atau direbus, dan pesan porsi sayur tambahan.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Makan+Luar', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(188, 'Pemanasan Ulang Makanan yang Aman', 'pemanasan-ulang-makanan', 'tips', '<p>Pastikan makanan dipanaskan ulang hingga benar-benar panas (>70Â°C) untuk membunuh bakteri. Jangan panaskan ulang makanan yang kaya protein berulang kali.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Reheat+Food', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(190, 'Mengelola Stres dan Makan Emosional', 'stres-makan-emosional', 'tips', '<p>Identifikasi pemicu makan emosional. Ganti kebiasaan makan saat stres dengan aktivitas lain, seperti minum air, berjalan kaki, atau meditasi.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Stres', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(192, 'Minuman Sehat Selain Air Putih', 'minuman-sehat', 'tips', '<p>Selain air putih, pilih teh tawar, air lemon, atau *infused water*. Hindari minuman kemasan yang tinggi gula dan pemanis buatan.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Minuman', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(194, 'Pola Makan Vegetarian untuk Pemula', 'pola-makan-vegetarian-pemula', 'tips', '<p>Mulai dengan mengganti satu kali makan daging dengan sumber protein nabati. Pastikan asupan Vitamin B12 tetap tercukupi.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Vegetarian+Tips', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(196, 'Tips Memilih Buah dan Sayur Terbaik', 'memilih-buah-sayur', 'tips', '<p>Pilih buah dan sayur yang berwarna cerah (kaya antioksidan). Beli produk lokal dan musiman untuk harga yang lebih baik dan nutrisi maksimal.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Buah+Sayur', '3 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(198, 'Diet Rendah Gula untuk Kesehatan Jantung', 'diet-rendah-gula-jantung', 'tips', '<p>Mengurangi gula tambahan adalah langkah terbaik untuk jantung. Gula memicu inflamasi dan kenaikan berat badan, yang merupakan faktor risiko penyakit jantung.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Gula+Jantung', '4 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34'),
(200, 'Pentingnya *Mindful Eating*', '*mindful-eating*', 'tips', '<p>*Mindful eating* (makan penuh kesadaran) adalah fokus pada sensasi makan, bukan gangguan (TV/HP). Ini membantu mengenali sinyal kenyang dan menikmati makanan lebih baik.</p>', 'Tim LacakNutri', 'https://placehold.co/600x400/6B8E23/white?text=Mindful+Eating', '5 min', 0, 1, '2025-11-23 20:30:34', '2025-11-23 20:30:34');
